    this.client = {};
    this.selectedClients = [];
    this.submitted = false;
    this.cols = [];
    this.statuses = [];
    this.rowsPerPageOptions = [5, 10, 20];
    this.ufs = [];
    this.municipios = [];
  }
  ngOnInit() {
    this.cepService.buscarEstados().subscribe(ufs => {
      this.ufs = ufs;
    });
    this.clientService.getClients().subscribe(clients => {
      this.clients = clients;
      console.log(clients);
    });
    this.cols = [{
      field: 'client',
      header: 'Client'
    }, {
      field: 'endereco',
      header: 'Endereço'
    }, {
      field: 'telefone',
      header: 'Telefone'
    }, {
      field: 'cpf',
      header: 'CPF'
    }, {
      field: 'sexo',
      headed: 'Sexo'
    }];
  }
  getCep(cep) {
    this.cepService.buscar(cep).subscribe(cep => {
      this.client.logradouro = cep.logradouro;
      const estadoId = this.ufs.find(estado => estado.sigla == cep.uf);
      this.client.estado = estadoId;
      this.getMunicipios(estadoId.id);
      setTimeout(() => {
        const municipioId = this.municipios.find(cidade => cidade.nome == cep.localidade);
        this.client.municipio = municipioId;
      }, 500);
    });
  }
  getMunicipios(code) {
    this.cepService.buscarMunicipios(code).subscribe(municipios => {
      this.municipios = municipios;
    });
  }
  testeUF() {
    console.log("municipio", this.client.municipio);
  }
  openNew() {
    this.client = {};
    this.submitted = false;
    this.clientDialog = true;
  }
  deleteSelectedClients() {
    this.deleteClientsDialog = true;
  }
  editClient(client) {
    this.client = {
      ...client
    };
    this.clientDialog = true;
  }
  deleteClient(client) {
    this.deleteClientDialog = true;
    // this.client = { ...client };
    this.clientService.deleteClient(client.key);
    this.confirmDelete();
  }
  confirmDeleteSelected() {
    this.deleteClientsDialog = false;
    // this.clients = this.clients.filter(val => !this.selectedClients.includes(val));
    this.selectedClients.forEach(client => {
      this.clientService.deleteClient(client.key);
    });
    this.messageService.add({
